#face highlighting

import cv2
cascade = cv2.CascadeClassifier('Cascades/haarcascade_frontalface_alt2.xml')
img = cv2.imread("photo ex.jpeg")
while True:
    gray = cv2.cvtColor(img, cv2.COLOR_BGR2GRAY)
    faces =cascade.detectMultiScale(img, 1.1, 2)
    for x,y,w,h in faces:
        cv2.rectangle(img, (x,y), (x+w, y+h), (0,0,255), 3)
    cv2.imshow("kaushiki", img)
    if cv2.waitKey(0) == 27:
        cv2.destroyAllWindows()
        break

#Extraction of face

from numpy import asarray
from PIL import Image

def extract_face_from_image(image_path, required_size=(224, 224)):

  # load image and detect faces

    image = plt.imread(image_path)
    detector = MTCNN()
    faces = detector.detect_faces(image)

    face_images = []

    for face in faces:
        # extract the bounding box from the requested face
        x1, y1, width, height = face['box']
        x2, y2 = x1 + width, y1 + height

        # extract the face
        face_boundary = image[y1:y2, x1:x2]

        # resize pixels to the model size
        face_image = Image.fromarray(face_boundary)
        face_image = face_image.resize(required_size)
        face_array = asarray(face_image)
        face_images.append(face_array)

    return face_images

extracted_face = extract_face_from_image('kaushiki2.jpg')

#Display the face from the extracted faces

plt.imshow(extracted_face[0])
plt.show()


